def obtener_url_curso(clave_curso, dic_cursos):
    curso_encontrado = dic_cursos.get(clave_curso)

    if curso_encontrado:
        return curso_encontrado['pagina_curso']
    else:
        return None

def obtener_url_pago(clave_curso, dic_cursos):
    curso_encontrado = dic_cursos.get(clave_curso)

    if curso_encontrado:
        return curso_encontrado['url_curso_socio']
    else:
        return None
